trigger.body:
    v_exist NUMBER;
  BEGIN
   select count(*) into v_exist from ad_module where javapackage ilike 'ec.com.sidesoft.budget.payroll';
   IF v_exist > 0 THEN
        IF(:NEW.em_ssbp_sfb_budget_item_id is null)THEN
	       RAISE_APPLICATION_ERROR(-20000, '@sspr_Message@');

        END IF;
   END IF;
   END SSPR_MESSAGE_TRG
