function.body:
numero_dias NUMBER:= 0;
numero_mes NUMBER:=0;
contar_mes NUMBER:=0;
mes_actual NUMBER:=0;
anio_actual NUMBER:=0;
fecha_actual DATE;
fecha_finmes DATE;

r NUMBER(10,4);
t NUMBER(10,4);

begin

r := value_sqtr;
t := 0;
WHILE  (t != r) LOOP
t := r;
r := (value_sqtr/r + r)/2;
END LOOP;
return to_number(r);
END SCMF_SQRT
