function.body:
-- Logistice
    v_ResultStr VARCHAR2(2000):='';
    v_Message VARCHAR2(2000):='';
    v_Result NUMBER:=1; -- 0=failure
    v_Record_ID VARCHAR2(32);
    v_User_ID VARCHAR2(32):='0';
	lvl0		VARCHAR(32);
	lvl1		VARCHAR(32);
	v_reg NUMBER:=0;

	texto		TEXT;
	Error_prom	TEXT;
	errorm		TEXT;

    TYPE RECORD IS REF CURSOR;
    Cur_Parameter RECORD;
	Cur_Product			RECORD;
	Cur_MProductBom		RECORD;
	Cur_Family			RECORD;
	Cur_Price 			RECORD;
	Cur_MProductPrice 	RECORD;


BEGIN

    DBMS_OUTPUT.PUT_LINE('Updating PInstance - Processing ' || P_Instance_ID);
    v_ResultStr:='PInstanceNotFound';
    AD_UPDATE_PINSTANCE(P_Instance_ID, NULL, 'Y', NULL, NULL);

    BEGIN --BODY
        -- Get Parameters
        v_ResultStr:='ReadingParameters';
        FOR Cur_Parameter IN (
            SELECT i.Record_ID, i.AD_User_ID, p.ParameterName, p.P_String, p.P_Number, p.P_Date
            FROM AD_PInstance i
                LEFT JOIN AD_PInstance_Para p ON i.AD_PInstance_ID = p.AD_PInstance_ID
            WHERE i.AD_PInstance_ID = P_Instance_ID
            ORDER BY p.SeqNo
        ) LOOP
            v_Record_ID := Cur_Parameter.Record_ID;
            v_User_ID := Cur_Parameter.AD_User_ID;
        END LOOP; -- Get Parameter

		--Price

		FOR Cur_Price IN(
			select 
				m_product_id 
			from ssdah_mirror_price
			group by m_product_id 
		)LOOP

			DELETE FROM M_ProductPrice
			where m_product_id = Cur_Price.m_product_id;	

			FOR Cur_MProductPrice IN(
				select 
					*
				from ssdah_mirror_price
				where m_product_id = Cur_Price.m_product_id
			)LOOP

				select name 
				into Error_prom
				from M_PriceList_Version 
				where M_PriceList_Version_ID = Cur_MProductPrice.M_PriceList_Version_ID;


				INSERT INTO public.m_productprice(
					m_productprice_id, m_pricelist_version_id, m_product_id, ad_client_id,
					ad_org_id, isactive, created, createdby,
					updated, updatedby, pricelist, pricestd,
					pricelimit, cost, algorithm)
				VALUES (
					Cur_MProductPrice.ssdah_mirror_price_id, Cur_MProductPrice.m_pricelist_version_id, Cur_MProductPrice.m_product_id, Cur_MProductPrice.ad_client_id, 
					Cur_MProductPrice.ad_org_id, Cur_MProductPrice.isactive, Cur_MProductPrice.created, Cur_MProductPrice.createdby,
					Cur_MProductPrice.updated, Cur_MProductPrice.updatedby, Cur_MProductPrice.pricelist, Cur_MProductPrice.pricestd,
					Cur_MProductPrice.pricelimit, Cur_MProductPrice.cost, Cur_MProductPrice.algorithm);
			END LOOP;

			DELETE FROM ssdah_mirror_price
			where m_product_id = Cur_Price.m_product_id;


		END LOOP;

		-- RAISE LOG 'FIN';

        DBMS_OUTPUT.PUT_LINE('Updating PInstance - Finished ' || v_Message);
        AD_UPDATE_PINSTANCE(P_Instance_ID, NULL, 'N', v_Result, v_Message);

        RETURN;
    END; --BODY

    EXCEPTION WHEN OTHERS THEN
        v_ResultStr:= '@ERROR=' || SQLERRM;
        DBMS_OUTPUT.PUT_LINE( v_ResultStr) ;
		-- RAISE LOG '%', v_ResultStr ;

		SELECT SUBSTRING(SQLERRM, 2, LENGTH(SQLERRM) - 2)
		into errorm;

		select msgtext 
		into texto
		from ad_message_trl
		where ad_message_id in (select ad_message_id 
		from ad_message 
		where value = errorm);
		IF texto is not null then
			RAISE texto || ' Producto: '|| Error_prom;
		ELSE
			RAISE Error_prom || ' - 'v_ResultStr;
		END IF;
        ROLLBACK;
        AD_UPDATE_PINSTANCE(P_Instance_ID, NULL, 'N', 0, v_ResultStr);
END SSDAH_UPDATE_PRICES
