trigger.body:
v_count NUMBER:=0;

BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

    SELECT count(*) 
    INTO v_count 
    FROM ssdah_mmo_oc_family cf 
    WHERE cf.Ssdah_Mirrormo_ID = :new.Ssdah_Mirrormo_ID 
    AND cf.Ssdah_Ss_Main='Y' 
    AND cf.ssdah_mmo_oc_family_id NOT IN(:new.ssdah_mmo_oc_family_id) ;

    IF(INSERTING) THEN
	    IF (v_count>0) THEN
	      IF(:new.Ssdah_Ss_Main <> 'N') THEN
		RAISE_APPLICATION_ERROR(-20000, '@Swsoc_Only_One_Main_Family@');
	      END IF;    
	    END IF;	
    END IF;

    IF(UPDATING) THEN
	    IF (v_count>0) THEN
	      IF(:new.Ssdah_Ss_Main <> :old.Ssdah_Ss_Main) THEN
		RAISE_APPLICATION_ERROR(-20000, '@Swsoc_Only_One_Main_Family@');
	      END IF;    
	    END IF;
    END IF;       

END SSDAH_MRRFAMILY_UNIQWEB_TRG
