trigger.body:
v_count NUMBER:=0;

BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

    SELECT count(*) 
    INTO v_count 
    FROM obcombo_product cp 
    WHERE cp.obcombo_family_id = :new.obcombo_family_id 
    AND cp.em_ssrcb_main='Y'
    AND cp.obcombo_product_id NOT IN( :new.obcombo_product_id) ;

    IF(INSERTING) THEN
	    IF (v_count>0) THEN
	      IF(:new.em_ssrcb_main <> 'N') THEN
		RAISE_APPLICATION_ERROR(-20000, '@Ssrcb_Only_One_Main@');
	      END IF;    
	    END IF;	
    END IF;

    IF(UPDATING) THEN
	    IF (v_count>0) THEN
	      IF(:new.em_ssrcb_main <> :old.em_ssrcb_main) THEN
		RAISE_APPLICATION_ERROR(-20000, '@Ssrcb_Only_One_Main@');
	      END IF;    
	    END IF;
    END IF;     		


END SSRCB_PRINCIPAL_UNIQUE_TRG
