IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF (INSERTING OR UPDATING) THEN
SELECT COALESCE((select TRIM(EM_EEI_Email) from c_bpartner WHERE C_BPartner_id = :NEW.C_BPartner_id),'ND') INTO v_emailOLD FROM DUAL;
IF (v_emailOLD='ND') THEN
UPDATE C_BPartner SET EM_EEI_Eeioice = 'Y', EM_EEI_Email = :NEW.email WHERE C_BPartner_id = :NEW.C_BPartner_id;
END IF;
END IF;
END SREPA_PARTNERRETAIL_TRG
